package com.fallntic.jotaayumouride;import android.content.Intent;import android.content.pm.ActivityInfo;import android.os.Bundle;import android.view.Menu;import android.view.MenuInflater;import android.view.MenuItem;import android.view.View;import android.view.WindowManager;import android.widget.EditText;import androidx.appcompat.app.AppCompatActivity;import androidx.appcompat.widget.Toolbar;import com.hbb20.CountryCodePicker;public class LoginPhoneActivity extends AppCompatActivity implements View.OnClickListener {    private EditText editTextMobile;    CountryCodePicker ccp;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_login_phone);        setRequestedOrientation(ActivityInfo.SCREEN_ORIENTATION_PORTRAIT);        Toolbar toolbar = findViewById(R.id.toolbar);        toolbar.setLogo(R.mipmap.logo);        setSupportActionBar(toolbar);        editTextMobile = findViewById(R.id.editTextMobile);        ccp = findViewById(R.id.ccp);        ccp.registerCarrierNumberEditText(editTextMobile);        findViewById(R.id.buttonContinue).setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String mobile = editTextMobile.getText().toString().trim();                if (mobile.isEmpty()) {                    editTextMobile.setError("Numero telephone incorrect");                    editTextMobile.requestFocus();                    return;                } else if (mobile.contains("+")) {                    editTextMobile.setError("Ne pas inclure l'indicatif svp.");                    editTextMobile.requestFocus();                    return;                }                mobile = ccp.getFullNumberWithPlus();                Intent intent = new Intent(LoginPhoneActivity.this, VerifyPhoneActivity.class);                intent.putExtra("mobile", mobile);                startActivity(intent);            }        });        hideSoftKeyboard();        findViewById(R.id.textView_signUpEmail).setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.textView_signUpEmail:                finish();                startActivity(new Intent(this, LoginActivity.class));                break;        }    }    @Override    public boolean onCreateOptionsMenu(Menu menu) {        MenuInflater inflater = getMenuInflater();        inflater.inflate(R.menu.main_menu, menu);        MenuItem iconBack;        iconBack = menu.findItem(R.id.icon_back);        iconBack.setVisible(true);        return true;    }    @Override    public boolean onOptionsItemSelected(MenuItem item) {        switch (item.getItemId()) {            case R.id.icon_back:                finish();                startActivity(new Intent(this, HomeActivity.class));                break;            case R.id.instructions:                startActivity(new Intent(this, InstructionsActivity.class));                break;        }        return true;    }    public void hideSoftKeyboard(){        this.getWindow().setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_STATE_ALWAYS_HIDDEN);    }}